{"code":"function Carousel_02() {
  const [activeIndex, setActiveIndex] = React.useState(0);
  const goPrev = () => setActiveIndex(prev => (prev - 1 + images.length) % images.length);
  const goNext = () => setActiveIndex(prev => (prev + 1) % images.length);
  const goToSlide = index => setActiveIndex(index);

  React.useEffect(() => {
    const timer = setInterval(() => {
      setActiveIndex(prev => (prev + 1) % images.length);
    }, interval);
    return () => clearInterval(timer);
  }, [interval, images.length]);

  return (
    <div className="w-full bg-white dark:bg-slate-800">
      <div className="relative w-full mx-auto max-w-[1920px] min-h-[20rem] aspect-[16/6] flex items-center justify-center overflow-hidden rounded-xl shadow-md">
        {images.map((image, index) => (
          <div
            key={image.src}
            className={`absolute inset-0 transition-opacity duration-700 ease-in-out ${index === activeIndex ? 'opacity-100 z-20' : 'opacity-0 z-10'} w-full h-full`}
          >
            <EditableImg 
              propKey={`images_${index}_src`} 
              src={image.src} 
              className="w-full h-full object-cover aspect-[16/6] rounded-xl"
            />

            <div className="absolute inset-0 flex items-center justify-center">
              <div className="bg-white/50 backdrop-blur-sm rounded-lg p-8 max-w-lg text-center">
                <h2 className="text-4xl font-bold text-slate-800 mb-4">
                  <EditableText propKey={`storyTitle`}>{storyTitle}</EditableText>
                </h2>
                <div className="mb-4">
                  <EditableImg
                    propKey={`images_${index}_avatar`}
                    src={image.avatar}
                    className="w-20 h-20 rounded-full border-4 border-white shadow-lg object-cover mx-auto"
                  />
                </div>
                <h1 className="text-3xl font-bold text-slate-800 drop-shadow-lg mb-3">
                  <EditableText propKey={`images_${index}_label`}>{image.label}</EditableText>
                </h1>
                <p className="text-lg text-slate-600 max-w-2xl mx-auto">
                  <EditableText propKey={`images_${index}_description`}>{image.description}</EditableText>
                </p>
              </div>
            </div>
          </div>
        ))}
        <EditableButton
          className="absolute left-4 top-1/2 -translate-y-1/2 bg-white/70 hover:bg-white/90 transition-colors rounded-full p-2 flex items-center justify-center opacity-80 hover:opacity-100 backdrop-blur-md shadow-lg"
          type="button"
          onClick={goPrev}
        >
          <EditableIcon propKey={"prevSlideIcon"} icon={prevSlideIcon} iconLibrary={"FontAwesome"} className="text-2xl text-slate-800" />
          <span className="sr-only">
            <EditableText propKey={`buttonPrevious`}>{buttonPrevious}</EditableText>
          </span>
        </EditableButton>
        <EditableButton
          className="absolute right-4 top-1/2 -translate-y-1/2 bg-white/70 hover:bg-white/90 transition-colors rounded-full p-2 flex items-center justify-center opacity-80 hover:opacity-100 backdrop-blur-md shadow-lg"
          type="button"
          onClick={goNext}
        >
